<script>
export default {
  components: {},

  mounted() {
    this.svgScroll()
  },

  methods: {
    svgScroll() {
      // Svg elements and position
      const path = document.querySelector('.illu_path')
      const illu = document.querySelector('#illustration')

      window.addEventListener('scroll', () => {
        // Get how much of the element (0->1) has been scrolled
        const scrollPosition =
          document.documentElement.scrollTop +
          document.documentElement.clientHeight -
          illu.offsetTop

        let progress =
          (scrollPosition - path.clientHeight / 2) /
          (illu.scrollHeight - path.clientHeight / 2)

        // Draw path
        if (progress < 0) progress = 0
        else if (progress > 1) progress = 1
        path.style.opacity = 1
        const pathLength = path.getTotalLength()
        path.style.strokeDasharray = pathLength
        path.style.strokeDashoffset = pathLength
        const drawLength = pathLength * progress
        path.style.strokeDashoffset = pathLength - drawLength
      })
    }
  }
}
</script>

